import { Button, Modal, Form, Input, Table, Popconfirm,Typography,InputNumber } from "antd";
import { AudioOutlined } from '@ant-design/icons';
import { useState } from "react";
import { SearchOutlined } from '@ant-design/icons';
import AddStudent from "./addStudent";
import tableListStudent from "./listStudent";


function Student() {
  const { Search } = Input;

  // let listStudent = tableListStudent;
  let totalStudent = tableListStudent.length
  for (let i = 0; i < tableListStudent.length; i++) {
    tableListStudent[i].index = i + 1;

  }
// edit activiti
const EditableCell = ({
  editing,
  dataIndex,
  title,
  inputType,
  record,
  index,
  children,
  ...restProps
}) => {
  const inputNode = inputType === "number" ? <InputNumber /> : <Input />

  return (
    <td {...restProps}>
      {editing ? (
        <Form.Item
          name={dataIndex}
          style={{ margin: 0 }}
          rules={[
            {
              required: true,
              message: `Please Input ${title}!`
            }
          ]}
        >
          {inputNode}
        </Form.Item>
      ) : (
        children
      )}
    </td>
  )
}
const [editingKey, setEditingKey] = useState('');

const isEditing = record => record.key === editingKey

const edit = record => {
  form.setFieldsValue({ name: "", dob: "", gender: "",class: "", hometown: "", gender: "", ...record })
  setEditingKey(record.key)
}

  const cancel = () => {
    setEditingKey('');
  };

  const save = async key => {
    try {
      const row = await form.validateFields()

      const newData = [...listStudent]
      const index = newData.findIndex(item => key === item.key)
      if (index > -1) {
        const item = newData[index]
        newData.splice(index, 1, {
          ...item,
          ...row
        })
        setListStudent(newData)
        setEditingKey("")
      } else {
        newData.push(row)
        setListStudent(newData)
        setEditingKey("")
      }
    } catch (errInfo) {
      console.log("Validate Failed:", errInfo)
    }
  }

  // seetting for search
  const suffix = (
    <AudioOutlined
      style={{

        fontSize: 16,
        color: '#1890ff',
      }}
    />
  );


  const columns = [
    // {
    //   title: "Index",
    //   dataIndex: "index",
    //   width: 30,
    //   align: "center"

    // },
    {
      title: "Name",
      dataIndex: "name",
      width: 170,
      align: "left",

      render: _ => <a>{_}</a>,
    },
    {
      title: "Day of birth",
      dataIndex: "dob",
      align: "center",
      width: 120,

    },
    {
      title: "Gender",
      dataIndex: "gender",
      align: "center",
      width: 100,

    },
    {
      title: "Class",
      dataIndex: "class",
      align: "center",
      width: 100,

    },
    {
      title: "Hometown",
      dataIndex: "hometown",
      align: "center",
      width: 120,

    },
    {
      title: "Phone Number",
      dataIndex: "phone",
      align: "center",
      width: 120,

    },

    {
      title: "Action",
      align: "center",
      key: "option",
      width: 200,
      valueType: "option",
      render: (text, record) => [
        
        listStudent.length >= 1 ? (
          <Popconfirm
            title="Sure to delete?"
            onConfirm={() => handleDelete(record.index)}
          >
            <a>Delete</a>
          </Popconfirm>
        ) : null,

        ]
    },
    {
      render: (_, record) => {
        const editable = isEditing(record);
        return editable ? (
          <span>
            <Typography.Link onClick={() => save(record.key)} style={{ marginRight: 8 }}>
              Save
            </Typography.Link>
            <Popconfirm title="Sure to cancel?" onConfirm={cancel}>
              <a>Cancel</a>
            </Popconfirm>
          </span>
        ) : (
          <Typography.Link disabled={editingKey !== ''} onClick={() => edit(record)}>
            Edit
          </Typography.Link>
        );
      }
      
    }
  ]
  // action delete
  const handleDelete = (index) => {

    const newData = listStudent.filter(item => item.index !== index)
    setListStudent(newData);

  }
  const [form] = Form.useForm();

  const [searchText, setSearchText] = useState('');
  const [listStudent, setListStudent] = useState(tableListStudent)


  // const [tableListStudentArray, settableListStudent] = useState(listStudent)





  // active modal
  const [isModalVisible, setIsModalVisible] = useState(false);
  const [isError, setIsError] = useState(false)
  const showModal = () => {
    setIsModalVisible(true);
  };

  const handleOk = () => {
    // form.submit();
    form.resetFields();
    setIsError(false);
  };

  const handleCancel = () => {
    form.resetFields();
    setIsError(false);
    setIsModalVisible(false);
  };

  const onFinish = (values) => {
    if (values.name === undefined || values.dob === undefined || values.gender === undefined || values.class === undefined || values.phone === undefined || values.hometown === undefined) {
      console.log("Error");
      setIsError(true);
      setIsModalVisible(true);
    } else {
      const newStudent = {
        ...values,
        index: totalStudent + 1
      }

      listStudent.push(newStudent);
      setListStudent(listStudent);
      totalStudent = totalStudent + 1;
      console.log("PASS");
      setIsError(false);
      form.resetFields();
      setIsModalVisible(false);
    }



  };
  const mergedColumns = columns.map(col => {
    if (!col.editable) {
      return col
    }
    return {
      ...col,
      onCell: record => ({
        record,
        inputType: col.dataIndex === "text",
        dataIndex: col.dataIndex,
        title: col.title,
        editing: isEditing(record)
      })
    }
  })
  return (
    <div classname="studentContainer">

      <div className="justify-between flex">



        {/* search activities */}
        <Search className="w-80"
          placeholder="Search in here..."
          enterButton={
            <Button
              style={{
                color: "#fff",
                backgroundColor: "#1677ff",
                boxShadow: "0 2px 0 rgba(5, 145, 255, 0.1)"
              }} >
              Search
            </Button>

          }
          value={searchText}
          onChange={(e) => {
            var text = e.target.value;

            return setSearchText(text)
          }}
          onPressEnter={(value) => setSearchText(value)}

          size="large"
          suffix={suffix}
        />

        {/* add new student button */}
        <Button className="mr-12" onClick={showModal}>
          Add New Student
        </Button>


      </div>
      <Modal
        title="Register New Student"
        open={isModalVisible}
        onOk={handleOk}
        onCancel={handleCancel}

        footer={[
          <Button key="back" onClick={handleCancel}>
            Cancel
          </Button>,
          <Button key="submit" onClick={() => form.submit()}>
            Submit
          </Button>,
        ]}
      >
        <Form form={form} onFinish={onFinish} >
          <Form.Item name="name" label="Name">
            <Input placeholder="Name" />
          </Form.Item>
          <Form.Item name="dob" label="Day of birthday">
            <Input placeholder="Day of birthday" />
          </Form.Item>
          <Form.Item name="gender" label="Gender">
            <Input placeholder="Gender" />
          </Form.Item>
          <Form.Item name="class" label="Class">
            <Input placeholder="Class" />
          </Form.Item>
          <Form.Item name="hometown" label="Hometown">
            <Input placeholder="Hometown" />
          </Form.Item>
          <Form.Item name="phone" label="Phone Number">
            <Input placeholder="Phone Number" />
          </Form.Item>

        </Form>
        {isError && (<p className="text-red-500" >Vui lòng nhập đầy đủ  thông tin!!</p>)}


      </Modal>
      {/* table student */}
      <Form form={form} component={false}>
      <Table
        columns={mergedColumns}
        onDelete={(index) => handleDelete(index)}
        rowClassName="editable-row"
        components={{
          body: {
            cell: EditableCell
          }
        }}
        dataSource={listStudent.filter((item) => item.name.toLowerCase().replace(/[àáảãạâầấẩẫậăằắẳẵặ]/g, "a")
          .replace(/[èéẻẽẹêềếểễệ]/g, "e")
          .replace(/[ìíỉĩị]/g, "i")
          .replace(/[òóỏõọôồốổỗộơờớởỡợ]/g, "o")
          .replace(/[ùúủũụưừứửữự]/g, "u")
