import { EllipsisOutlined } from "@ant-design/icons"
import {
    LightFilter,
    ProFormDatePicker,
    ProTable
} from "@ant-design/pro-components"
import { Button, Table } from "antd"
import { useState } from "react"
import en_US from 'antd/es/locale/en_US';

const tableListDataSource = [
    {
        name: "Lê Bùi Văn",
        dob: "22/06/2002",
        gender: "Nam",
        class: "A",
        phone: "0123456789",
        hometown: "Hà Nội",
        subjects: ["Toán", "Vật lý", "Tiếng Anh"]
    },
    {
        name: "Nguyễn Thị Kim Ngân",
        dob: "09/03/2003",
        gender: "Nữ",
        class: "B",
        phone: "0112345678",
        hometown: "Bắc Ninh",
        subjects: ["Văn", "Hóa học", "Lịch sử"]
    },
    {
        name: "Trần Văn Minh",
        dob: "05/11/2002",
        gender: "Nam",
        class: "C",
        phone: "0109876543",
        hometown: "Thái Bình",
        subjects: ["Toán", "Vật lý", "Sinh học"]
    },
    {
        name: "Phạm Thị Thu",
        dob: "17/02/2002",
        gender: "Nữ",
        class: "A",
        phone: "0198765432",
        hometown: "Hải Phòng",
        subjects: ["Văn", "Địa lí", "Tiếng Anh"]
    },
    {
        name: "Nguyễn Văn Hoàn",
        dob: "12/09/2003",
        gender: "Nam",
        class: "B",
        phone: "0187654321",
        hometown: "Hưng Yên",
        subjects: ["Toán", "Vật lý", "Hóa học"]
    },
    {
        name: "Bùi Ngọc Ánh",
        dob: "08/06/2002",
        gender: "Nữ",
        class: "C",
        phone: "0176543210",
        hometown: "Nghệ An",
        subjects: ["Toán", "Hóa học", "Sinh học"]
    }
]
for (let i = 0; i < 6; i++) {
    tableListDataSource[i].index = i + 1;
}

const enLocale = {
    columnSetting: 'Settings',
}

const columns = [
    {
        title: "Index",
        dataIndex: "index",
        width: 60,
        align: "center"
    },
    {
        title: "Name",
        dataIndex: "name",
        width: 200,
        align: "left",
        render: _ => <a>{_}</a>
    },
    {
        title: "Day of birth",
        dataIndex: "dob",
        align: "center",
        width: 120,

    },
    {
        title: "Gender",
        dataIndex: "gender",
        align: "center",
        width: 100,

    },
    {
        title: "Class",
        dataIndex: "class",
        align: "center",
        width: 100,

    },
    {
        title: "Hometown",
        dataIndex: "hometown",
        align: "center",
        width: 120,

    },
    {
        title: "Phone Number",
        dataIndex: "phone",
        align: "center",
        width: 120,

    },
    {
        title: "Subject",
        dataIndex: "subjects",
        align: "center",
        render: (subjects) => (
            <>
                {

                    subjects.join(' - ')
                }
            </>
        )
    },
    {
        title: "Action",
        align: "center",
        key: "option",
        width: 120,
        valueType: "option",
        render: () => [
            <a key="edit">Edit</a>,
            <a key="delete">Delete</a>,
        ]
    }
]

function Student() {
    const [searchText, setSearchText] = useState("");
    const [searchValue, setSearchValue] = useState('');
    const filteredData = [{
        name: "Bùi Ngọc Ánh",
        dob: "08/06/2002",
        gender: "Nữ",
        class: "C",
        phone: "0176543210",
        hometown: "Nghệ An",
        subjects: ["Toán", "Hóa học", "Sinh học"]
    }]


    return (

        <ProTable
            locale={en_US}
            columns={columns}
            request={(params, sorter, filter,searchValue) => {
                console.log(searchValue)
                return Promise.resolve(
                   {
                    data: filteredData,
                    total: tableListDataSource.length,
                    success: true
                   }
                )
            }}
            // request={async (params) => {
            //     // Get the search query from the params object
            //     const { keyword } = params;
            //     // Filter the data based on the search query
            //     const filteredData = tableListDataSource.filter((item) => item.name.includes(keyword));
            //     return { data: filteredData };
            //   }
            // }


            toolbar={{
                search: {
                    placeholder: "Search in here...",
                    onSearch: (value) => {
                      // do something with the search value, like filter the data source
                      

                    },
                    onChange: (value)=> {
                        
                        const filteredData = tableListDataSource.filter((item) => item.name.includes(value));
                        console.log(filteredData);
                    }

                  },

                actions: [
                    <Button
                        key="primary"
                        onClick={() => {
                            alert("add")
                        }}
                    >
                        Add
                    </Button>
                ]

            }}
           
            pagination={{
                // show total number page
                showTotal: (total, range) => ` ${range[0]}-${range[1]}/ Tổng ${total}`,
                defaultPageSize: 5, // số dòng mỗi trang
                // // showSizeChanger: true, // hiển thị dropdown chọn số dòng
                // pageSizeOptions: ["5", "10", "20"] //  danh sách số dòng mỗi trang


            }}
            
            options={{
                setting: {
                    search: true,
                    title: "Cài đặt"
                },
                reload: {
                    title: "Refresh"
                }

            }}

            // attribute turn off search bar in toolbar
            search={false}
        // define format day data
        // dateFormatter="number"

        />
    )
}
export default Student
