import { ProTable } from "@ant-design/pro-components"
import { useState } from "react"
import {Button} from 'antd'


const tableListDataSource = [{
    name: "Lê Bùi Văn",
    dob: "22/06/2002",
    gender: "Nam",
    class: "A",
    phone: "0123456789",
    hometown: "Hà Nội",
    subject: ["Toán", "Vật lý", "Tiếng Anh"]
  },
  {
    name: "Nguyễn Thị Kim Ngân",
    dob: "09/03/2003",
    gender: "Nữ",
    class: "B",
    phone: "0112345678",
    hometown: "Bắc Ninh",
    subject: ["Văn", "Hóa học", "Lịch sử"]
  },
  {
    name: "Trần Văn Minh",
    dob: "05/11/2002",
    gender: "Nam",
    class: "C",
    phone: "0109876543",
    hometown: "Thái Bình",
    subject: ["Toán", "Vật lý", "Sinh học"]
  },
  {
    name: "Phạm Thị Thu",
    dob: "17/02/2002",
    gender: "Nữ",
    class: "A",
    phone: "0198765432",
    hometown: "Hải Phòng",
    subject: ["Văn", "Địa lí", "Tiếng Anh"]
  },
  {
    name: "Nguyễn Văn Hoàn",
    dob: "12/09/2003",
    gender: "Nam",
    class: "B",
    phone: "0187654321",
    hometown: "Hưng Yên",
    subject: ["Toán", "Vật lý", "Hóa học"]
  },
  {
    name: "Bùi Ngọc Ánh",
    dob: "08/06/2002",
    gender: "Nữ",
    class: "C",
    phone: "0176543210",
    hometown: "Nghệ An",
    subject: ["Toán", "Hóa học", "Sinh học"]
  }]
for (let i = 0;i < tableListDataSource.length;i++) {
    tableListDataSource[i].index = i+1;
}

const columns = [
    // {
    //     title: "Index",
    //     key: "name2",
    //     dataIndex: "index",
    //     valueType: "text",
       
    // },
    {
        title: "Name",
        key: "name1",
        dataIndex: "name",
        valueType: "text",
       
    },
    {
        title: "Day of birth",
        key: "dob",
        dataIndex: "dob",
        valueType: "date",
        hideInSetting: true
    },
    {
        title: "Hometown",
        dataIndex: "hometown",
        key: "address"
    },
    {
        title: "Action",
        key: "option",
        width: 120,
        valueType: "option",
        render: () => [<a key="1">Edit</a>, <a key="2">Delete</a>]
    },

]

function Student() {
    const [columnsStateMap, setColumnsStateMap] = useState({
        name: {
            show: false,
            order: 2
        }
    })
    const [selectedRows, setSelectedRows] = useState([]);

    const handleSelectionChange = (keys, rows) => {
      setSelectedRows(rows);
    };
    return (
        <div>
             <ProTable
      columns={columns}
      dataSource={tableListDataSource}
      rowSelection={{
        type: 'checkbox',
        onChange: handleSelectionChange,
      }}
      pagination={{
        pageSize: 10,
      }}
      search={{
        placeholder: 'Tìm kiếm...',
      }}
      dateFormatter="string"
      rowKey="id"
      options={{
        fullScreen: true,
        setting: true,
      }}
      headerTitle="Danh sách người dùng"
      toolBarRender={() => [
        <Button key="add" type="primary">
          Thêm mới
        </Button>,
        <Button key="delete" danger disabled={selectedRows.length === 0}>
          Xóa
        </Button>,
      ]}
    />
        </div>

    )

}

export default Student
