import { Table, Button, Input, Space,Form } from "antd";
import { AudioOutlined } from '@ant-design/icons';
import { useState } from "react";
import { SearchOutlined } from '@ant-design/icons';

const { Search } = Input;

var tableListStudent = [
  {
    name: "Lê Bùi Văn",
    dob: "22/06/2002",
    gender: "Nam",
    class: "A",
    phone: "0123456789",
    hometown: "Hà Nội",
    subjects: ["Toán", "Vật lý", "Tiếng Anh"]
  },
  {
    name: "Nguyễn Thị Kim Ngân",
    dob: "09/03/2003",
    gender: "Nữ",
    class: "B",
    phone: "0112345678",
    hometown: "Bắc Ninh",
    subjects: ["Văn", "Hóa học", "Lịch sử"]
  },
  {
    name: "Trần Văn Minh",
    dob: "05/11/2002",
    gender: "Nam",
    class: "C",
    phone: "0109876543",
    hometown: "Thái Bình",
    subjects: ["Toán", "Vật lý", "Sinh học"]
  },
  {
    name: "Phạm Thị Thu",
    dob: "17/02/2002",
    gender: "Nữ",
    class: "A",
    phone: "0198765432",
    hometown: "Hải Phòng",
    subjects: ["Văn", "Địa lí", "Tiếng Anh"]
  },
  {
    name: "Nguyễn Văn Hoàn",
    dob: "12/09/2003",
    gender: "Nam",
    class: "B",
    phone: "0187654321",
    hometown: "Hưng Yên",
    subjects: ["Toán", "Vật lý", "Hóa học"]
  },
  {
    name: "Bùi Ngọc Ánh",
    dob: "08/06/2002",
    gender: "Nữ",
    class: "C",
    phone: "0176543210",
    hometown: "Nghệ An",
    subjects: ["Toán", "Hóa học", "Sinh học"]
  }
]
let totalStudent = tableListStudent.length
for (let i = 0; i < tableListStudent.length; i++) {
  tableListStudent[i].index = i + 1;
}
const columns = [
  {
    title: "Index",
    dataIndex: "index",
    width: 30,
    align: "center"

  },
  {
    title: "Name",
    dataIndex: "name",
    width: 170,
    align: "left",

    render: _ => <a>{_}</a>,
  },
  {
    title: "Day of birth",
    dataIndex: "dob",
    align: "center",
    width: 120,

  },
  {
    title: "Gender",
    dataIndex: "gender",
    align: "center",
    width: 100,

  },
  {
    title: "Class",
    dataIndex: "class",
    align: "center",
    width: 100,

  },
  {
    title: "Hometown",
    dataIndex: "hometown",
    align: "center",
    width: 120,

  },
  {
    title: "Phone Number",
    dataIndex: "phone",
    align: "center",
    width: 120,

  },
  {
    title: "Subject",
    dataIndex: "subjects",
    align: "center",
    render: (subjects) => (
      <>
        {

          subjects.join(' - ')
        }
      </>
    )
  },
  {
    title: "Action",
    align: "center",
    key: "option",
    width: 200,
    valueType: "option",
    render: (text,record) => [
      <Button >Edit</Button>,
      <Button onClick={() => {
        console.log(tableListStudent[record.index-1])
        delete tableListStudent[record.index-1];
      }} >Delete</Button>,
    ]
  }
]
// delete 
const handleDelete = (index) => {
 
  return index 
  
};
// seetting for search
const suffix = (
  <AudioOutlined
    style={{

      fontSize: 16,
      color: '#1890ff',
    }}
  />
);

function addStudent() {
  return (
    <Form form={form} onFinish={onFinish} layout="inline">
        <Form.Item name="name">
          <Input placeholder="Name" />
        </Form.Item>
        <Form.Item name="age">
          <Input placeholder="Age" />
        </Form.Item>
        <Form.Item name="address">
          <Input placeholder="Address" />
        </Form.Item>
        <Form.Item>
          <Button htmlType="submit">Add</Button>
        </Form.Item>
      </Form>
  )
}

function Student() {
  const [searchText, setSearchText] = useState('');
  const [searchedColumn, setSearchedColumn] = useState('');
  const [tableStudent, settableStudent] = useState(tableListStudent);


  
  const getColumnSearchProps = (dataIndex) => ({
    // tạo một ô tìm kiếm 
    filterDropdown: ({ setSelectedKeys, selectedKeys, confirm, clearFilters }) => (
      <div style={{ padding: 8 }}>
        <Input
          placeholder={`Search ${dataIndex}`}
          value={selectedKeys[0]}
          onChange={(e) => setSelectedKeys(e.target.value ? [e.target.value] : [])}
          onPressEnter={() => confirm()}
          style={{ width: 188, marginBottom: 8, display: 'block' }}
        />
        <Space>
          <Button
            type="primary"
            onClick={() => confirm()}
            icon={<SearchOutlined />}
            size="small"
            style={{ width: 90 }}
          >
            Search
          </Button>
          <Button onClick={() => clearFilters()} size="small" style={{ width: 90 }}>
            Reset
          </Button>
        </Space>
      </div>
    ),
    // tạo một icon để biểu thị trạng thái của ô tìm kiếm
    filterIcon: (filtered) => <SearchOutlined style={{ color: filtered ? '#1890ff' : undefined }} />,
    //xử lý việc tìm kiếm.
    onFilter: (value, record) =>
      record[dataIndex] ? record[dataIndex].toString().toLowerCase().includes(value.toLowerCase()) : '',
    //   xử lý việc hiển thị ô tìm kiếm.
    onFilterDropdownVisibleChange: (visible) => {
      if (visible) {
        setTimeout(() => {
          document.getElementById('search-input').select();
        }, 100);
      }
    },
    // hiển thị kết quả tìm kiếm cho cột
    render: (text) =>
      searchedColumn === dataIndex ? (
        <span style={{ fontWeight: 'bold' }}>{text}</span>
      ) : (
        text
      ),
  });
  const handleSearch = (selectedKeys, confirm, dataIndex) => {
    confirm();
    setSearchText(selectedKeys[0]);
    setSearchedColumn(dataIndex);
  };

  const handleReset = (clearFilters) => {
    clearFilters();
    setSearchText('');
  };
  
  return (
    <div classname = "studentContainer">
      <div className="justify-between flex">
        <Search className="w-80"
          placeholder="Search in here..."
          enterButton={
            <Button
              style={{
                color: "#fff",
                backgroundColor: "#1677ff",
                boxShadow: "0 2px 0 rgba(5, 145, 255, 0.1)"
              }} >
              Search
            </Button>

          }
          value={searchText}
          onChange={(e) => {
            var text = e.target.value;
          
            return setSearchText(text)
          }}
          onPressEnter={(value) => setSearchText(value)}

          size="large"
          suffix={suffix}
        />
        <Button className="mr-12" onClick={addStudent}> Add</Button>
      </div>


      <Table
        columns={columns}
        dataSource={tableListStudent.filter((item) => item.name.toLowerCase().replace(/[àáảãạâầấẩẫậăằắẳẵặ]/g, "a")
          .replace(/[èéẻẽẹêềếểễệ]/g, "e")
          .replace(/[ìíỉĩị]/g, "i")
          .replace(/[òóỏõọôồốổỗộơờớởỡợ]/g, "o")
          .replace(/[ùúủũụưừứửữự]/g, "u")
          .replace(/[ỳýỷỹỵ]/g, "y")
          .replace(/[đ]/g, "d")
          .replace(/\s+/g, " ")
          .replace(/[^A-Za-z0-9 ]/g, "").includes(searchText.toLowerCase().replace(/[àáảãạâầấẩẫậăằắẳẵặ]/g, "a")
          .replace(/[èéẻẽẹêềếểễệ]/g, "e")
          .replace(/[ìíỉĩị]/g, "i")
          .replace(/[òóỏõọôồốổỗộơờớởỡợ]/g, "o")
          .replace(/[ùúủũụưừứửữự]/g, "u")
          .replace(/[ỳýỷỹỵ]/g, "y")
          .replace(/[đ]/g, "d")
          .replace(/\s+/g, " ")
          .replace(/[^A-Za-z0-9 ]/g, "")))}
        pagination={{
          showTotal: (total, range) => ` ${range[0]}-${range[1]}/ Tổng ${total}`,
          defaultPageSize: 5

        }}


      />
    </div>



  )
}
export default Student
