import { useState } from 'react';
import { ProTable, Button } from 'antd';

const columns = [
  {
    title: 'ID',
    dataIndex: 'id',
    sorter: (a, b) => a.id - b.id,
    width: 100,
  },
  {
    title: 'Tên',
    dataIndex: 'name',
    sorter: (a, b) => a.name.localeCompare(b.name),
    width: 200,
    render: (text, record) => <a href={`/users/${record.id}`}>{text}</a>,
  },
  {
    title: 'Email',
    dataIndex: 'email',
    width: 300,
  },
  {
    title: 'Tuổi',
    dataIndex: 'age',
    sorter: (a, b) => a.age - b.age,
    width: 100,
  },
];

const data = [
  { id: 1, name: 'John', email: 'john@gmail.com', age: 25 },
  { id: 2, name: 'Alice', email: 'alice@yahoo.com', age: 30 },
  { id: 3, name: 'Bob', email: 'bob@hotmail.com', age: 20 },
];

const MyTable = () => {
  const [selectedRows, setSelectedRows] = useState([]);

  const handleSelectionChange = (keys, rows) => {
    setSelectedRows(rows);
  };

  return (
    <ProTable
      columns={columns}
      dataSource={data}
      rowSelection={{
        type: 'checkbox',
        onChange: handleSelectionChange,
      }}
      pagination={{
        pageSize: 10,
      }}
      search={{
        placeholder: 'Tìm kiếm...',
      }}
      dateFormatter="string"
      rowKey="id"
      options={{
        fullScreen: true,
        setting: true,
      }}
      headerTitle="Danh sách người dùng"
      toolBarRender={() => [
        <Button key="add" type="primary">
          Thêm mới
        </Button>,
        <Button key="delete" danger disabled={selectedRows.length === 0}>
          Xóa
        </Button>,
      ]}
    />
  );
};

export default Student;
