import "./index.less"

import React, { useEffect, useRef, useState } from "react"
import {
  Table,
  ConfigProvider,
  Card,
  Space,
  Typography,
  Empty,
  Tooltip
} from "antd"
import classNames from "classnames"
import useMergeValue from "use-merge-value"
import { stringify } from "use-json-comparison"
import { ConfigConsumer } from "antd/lib/config-provider"

import { noteOnce } from "rc-util/lib/warning"
import { IntlProvider, IntlConsumer, useIntl } from "./component/intlContext"
import useFetchData from "./useFetchData"
import Container from "./container"
import Toolbar from "./component/toolBar"
import Alert from "./component/alert"
import FormSearch from "./form"
import get, {
  parsingText,
  parsingValueEnumToArray,
  checkUndefinedOrNull,
  useDeepCompareEffect,
  genColumnKey,
  removeObjectNull,
  ObjToMap,
  reduceWidth
} from "./component/util"
import defaultRenderText from "./defaultRender"
import ErrorBoundary from "./component/ErrorBoundary"

const mergePagination = (pagination = {}, action, intl) => {
  if (pagination === false) {
    return {}
  }
  let defaultPagination = pagination || {}
  const { current, pageSize } = action
  if (pagination === true) {
    defaultPagination = {}
  }
  return {
    showTotal: (all, range) =>
      `${intl.getMessage("pagination.total.range", "ç¬¬")} ${range[0]}-${
        range[1]
      } ${intl.getMessage(
        "pagination.total.total",
        "æ¡/æ€»å…±"
      )} ${all} ${intl.getMessage("pagination.total.item", "æ¡")}`,
    showSizeChanger: true,
    total: action.total,
    ...defaultPagination,
    current,
    pageSize,
    onChange: (page, newPageSize) => {
      // pageSize æ”¹å˜ä¹‹åå°±æ²¡å¿…è¦åˆ‡æ¢é¡µç 
      if (newPageSize !== pageSize && current !== page) {
        action.setPageInfo({ pageSize: newPageSize, page })
      } else {
        if (newPageSize !== pageSize) {
          action.setPageInfo({ pageSize: newPageSize })
        }
        if (current !== page) {
          action.setPageInfo({ page })
        }
      }

      const { onChange } = pagination
      if (onChange) {
        onChange(page, newPageSize || 20)
      }
    }
  }
}

/**
 * ç”Ÿæˆ Ellipsis çš„ tooltip
 * @param dom
 * @param item
 * @param text
 */
const genEllipsis = (dom, item, text) => {
  if (!item.ellipsis) {
    return dom
  }
  return (
    <Tooltip title={text}>
      <span>{dom}</span>
    </Tooltip>
  )
}

const genCopyable = (dom, item) => {
  if (item.copyable || item.ellipsis) {
    return (
      <Typography.Paragraph
        style={{
          width: reduceWidth(item.width),
          margin: 0,
          padding: 0
        }}
        copyable={item.copyable}
        ellipsis={item.ellipsis}
      >
        {dom}
      </Typography.Paragraph>
    )
  }
  return dom
}

/**
 * è¿™ä¸ªç»„ä»¶è´Ÿè´£å•å…ƒæ ¼çš„å…·ä½“æ¸²æŸ“
 * @param param0
 */
const columnRender = ({ item, text, row, index, columnEmptyText, counter }) => {
  const { action } = counter
  const { renderText = val => val, valueEnum = {} } = item
  if (!action.current) {
    return null
  }

  const renderTextStr = renderText(
    parsingText(text, ObjToMap(valueEnum)),
    row,
    index,
    action.current
  )
  const textDom = defaultRenderText(
    renderTextStr,
    item.valueType || "text",
    index,
    row,
    columnEmptyText
  )

  const dom = genEllipsis(
    genCopyable(textDom, item),
    item,
    renderText(
      parsingText(text, ObjToMap(valueEnum), true),
      row,
      index,
      action.current
    )
  )

  if (item.render) {
    const renderDom = item.render(dom, row, index, action.current)

    // å¦‚æœæ˜¯åˆå¹¶å•å…ƒæ ¼çš„ï¼Œç›´æ¥è¿”å›å¯¹è±¡
    if (
      renderDom &&
      typeof renderDom === "object" &&
      renderDom.props &&
      renderDom.props.colSpan
    ) {
      return renderDom
    }

    if (renderDom && item.valueType === "option" && Array.isArray(renderDom)) {
      return <Space>{renderDom}</Space>
    }
    return renderDom
  }
  return checkUndefinedOrNull(dom) ? dom : null
}

/**
 * è½¬åŒ– columns åˆ° pro çš„æ ¼å¼
 * ä¸»è¦æ˜¯ render æ–¹æ³•çš„è‡ªè¡Œå®ç°
 * @param columns
 * @param map
 * @param columnEmptyText
 */
const genColumnList = (columns, map, counter, columnEmptyText) =>
  columns
    .map((item, columnsIndex) => {
      const { key, dataIndex, title, filters = [] } = item
      const columnKey = genColumnKey(key, dataIndex, columnsIndex)
      const config = columnKey
        ? map[columnKey] || { fixed: item.fixed }
        : { fixed: item.fixed }
      const valueEnum = ObjToMap(item.valueEnum)
      const tempColumns = {
        onFilter: (value, record) => {
          let recordElement = get(record, item.dataIndex || "")
          if (typeof recordElement === "number") {
            recordElement = recordElement.toString()
          }
          const itemValue = String(recordElement || "")
          return String(itemValue) === String(value)
        },
        index: columnsIndex,
        ...item,
        title:
          title && typeof title === "function" ? title(item, "table") : title,
        valueEnum,
        filters:
          filters === true
            ? parsingValueEnumToArray(valueEnum).filter(
                valueItem => valueItem && valueItem.value !== "all"
              )
            : filters,
        ellipsis: false,
        fixed: config.fixed,
        width: item.width || (item.fixed ? 200 : undefined),
        children: item.children
          ? genColumnList(item.children, map, counter, columnEmptyText)
          : undefined,
        render: (text, row, index) =>
          columnRender({ item, text, row, index, columnEmptyText, counter })
      }
      if (!tempColumns.children || !tempColumns.children.length) {
        delete tempColumns.children
      }
      if (!tempColumns.dataIndex) {
        delete tempColumns.dataIndex
      }
      if (!tempColumns.filters || !tempColumns.filters.length) {
        delete tempColumns.filters
      }
      return tempColumns
    })
    .filter(item => !item.hideInTable)

/**
 * ğŸ† Use Ant Design Table like a Pro!
 * æ›´å¿« æ›´å¥½ æ›´æ–¹ä¾¿
 * @param props
 */
const ProTable = props => {
  const {
    request,
    className: propsClassName,
    params = {},
    defaultData = [],
    headerTitle,
    postData,
    pagination: propsPagination,
    actionRef,
    columns: propsColumns = [],
    toolBarRender,
    onLoad,
    onRequestError,
    style,
    tableStyle,
    tableClassName,
    columnsStateMap,
    onColumnsStateChange,
    options,
    search = true,
    rowSelection: propsRowSelection = false,
    beforeSearchSubmit = searchParams => searchParams,
    tableAlertRender,
    defaultClassName,
    formRef,
    type = "table",
    onReset = () => {},
    columnEmptyText = "-",
    ...rest
  } = props

  const [selectedRowKeys, setSelectedRowKeys] = useMergeValue([], {
    value: propsRowSelection ? propsRowSelection.selectedRowKeys : undefined
  })
  const [formSearch, setFormSearch] = useState(() => rest.form?.initialValues)
  const [selectedRows, setSelectedRows] = useState([])
  const [dataSource, setDataSource] = useState([])
  const [proFilter, setProFilter] = useState({})
  const [proSort, setProSort] = useState({})
  const rootRef = useRef(null)
  const fullScreen = useRef()

  /**
   * éœ€è¦åˆå§‹åŒ– ä¸ç„¶é»˜è®¤å¯èƒ½æŠ¥é”™
   * è¿™é‡Œå–äº† defaultCurrent å’Œ current
   * ä¸ºäº†ä¿è¯ä¸ä¼šé‡å¤åˆ·æ–°
   */
  const fetchPagination =
    typeof propsPagination === "object"
      ? propsPagination
      : { defaultCurrent: 1, defaultPageSize: 20, pageSize: 20, current: 1 }

  const action = useFetchData(
    async ({ pageSize, current }) => {
      if (!request) {
        return {
          data: props.dataSource || [],
          success: true
        }
      }
      const msg = await request(
        {
          current,
          pageSize,
          ...formSearch,
          ...params
        },
        proSort,
        proFilter
      )
      if (postData) {
        return { ...msg, data: postData(msg.data) }
      }
      return msg
    },
    defaultData,
    {
      defaultCurrent: fetchPagination.current || fetchPagination.defaultCurrent,
      defaultPageSize:
        fetchPagination.pageSize || fetchPagination.defaultPageSize,
      onLoad,
      onRequestError,
      effects: [
        stringify(params),
        stringify(formSearch),
        stringify(proFilter),
        stringify(proSort)
      ]
    }
  )

  useEffect(() => {
    fullScreen.current = () => {
      if (!rootRef.current || !document.fullscreenEnabled) {
        return
      }
      if (document.fullscreenElement) {
        document.exitFullscreen()
      } else {
        rootRef.current.requestFullscreen()
      }
    }
  }, [rootRef.current])

  action.fullScreen = fullScreen.current

  const intl = useIntl()

  const pagination =
    propsPagination !== false && mergePagination(propsPagination, action, intl)

  const counter = Container.useContainer()

  const onCleanSelected = () => {
    if (propsRowSelection && propsRowSelection.onChange) {
      propsRowSelection.onChange([], [])
    }
    setSelectedRowKeys([])
    setSelectedRows([])
  }

  useEffect(() => {
    // æ•°æ®æºæ›´æ–°æ—¶ å–æ¶ˆæ‰€æœ‰é€‰ä¸­é¡¹
    // onCleanSelected();
    setDataSource(request ? action.dataSource : props.dataSource || [])
  }, [props.dataSource, action.dataSource])

  /**
   *  ä¿å­˜ä¸€ä¸‹ propsColumns
   *  ç”Ÿæˆ form éœ€è¦ç”¨
   */
  useDeepCompareEffect(() => {
    counter.setProColumns(propsColumns)
  }, [propsColumns])

  counter.setAction(action)

  /**
   * è¿™é‡Œç”Ÿæˆactionçš„æ˜ å°„ï¼Œä¿è¯ action æ€»æ˜¯ä½¿ç”¨çš„æœ€æ–°
   * åªéœ€è¦æ¸²æŸ“ä¸€æ¬¡å³å¯
   */
  useEffect(() => {
    const userAction = {
      reload: async resetPageIndex => {
        const {
          action: { current }
        } = counter
        if (!current) {
          return
        }
        noteOnce(
          !!resetPageIndex,
          " reload çš„ resetPageIndex å°†ä¼šå¤±æ•ˆï¼Œå»ºè®®ä½¿ç”¨ reloadAndRestã€‚"
        )
        noteOnce(
          !!resetPageIndex,
          "reload resetPageIndex will remove and reloadAndRest is recommended."
        )

        // å¦‚æœä¸º trueï¼Œå›åˆ°ç¬¬ä¸€é¡µ
        if (resetPageIndex) {
          await current.resetPageIndex()
        }
        await current.reload()
      },
      reloadAndRest: async () => {
        const {
          action: { current }
        } = counter
        if (!current) {
          return
        }
        // reload ä¹‹åå¤§æ¦‚ç‡ä¼šåˆ‡æ¢æ•°æ®ï¼Œæ¸…ç©ºä¸€ä¸‹é€‰æ‹©ã€‚
        onCleanSelected()
        // å¦‚æœä¸º trueï¼Œå›åˆ°ç¬¬ä¸€é¡µ
        await current.resetPageIndex()
        await current.reload()
      },
      fetchMore: async () => {
        const {
          action: { current }
        } = counter
        if (!current) {
          return
        }
        await current.fetchMore()
      },
      reset: () => {
        const {
          action: { current }
        } = counter
        if (!current) {
          return
        }
        current.reset()
      },
      clearSelected: () => onCleanSelected()
    }
    if (actionRef && typeof actionRef === "function") {
      actionRef(userAction)
    }
    if (actionRef && typeof actionRef !== "function") {
      actionRef.current = userAction
    }
  }, [])

  /**
   * Table Column å˜åŒ–çš„æ—¶å€™æ›´æ–°ä¸€ä¸‹ï¼Œè¿™ä¸ªå‚æ•°å°†ä¼šç”¨äºæ¸²æŸ“
   */
  useDeepCompareEffect(() => {
    const tableColumn = genColumnList(
      propsColumns,
      counter.columnsMap,
      counter,
      columnEmptyText
    )
    if (tableColumn && tableColumn.length > 0) {
      counter.setColumns(tableColumn)
      // é‡æ–°ç”Ÿæˆkeyçš„å­—ç¬¦ä¸²ç”¨äºæ’åº
      counter.setSortKeyColumns(
        tableColumn.map((item, index) => {
          const key =
            genColumnKey(item.key, item.dataIndex, index) || `${index}`
          return `${key}_${item.index}`
        })
      )
    }
  }, [propsColumns])

  /**
   * è¿™é‡Œä¸»è¦æ˜¯ä¸ºäº†æ’åºï¼Œä¸ºäº†ä¿è¯æ›´æ–°åŠæ—¶ï¼Œæ¯æ¬¡éƒ½é‡æ–°è®¡ç®—
   */
  useDeepCompareEffect(() => {
    const keys = counter.sortKeyColumns.join(",")
    let tableColumn = genColumnList(
      propsColumns,
      counter.columnsMap,
      counter,
      columnEmptyText
    )
    if (keys.length > 0) {
      // ç”¨äºå¯è§†åŒ–çš„æ’åº
      tableColumn = tableColumn.sort((a, b) => {
        const { fixed: aFixed, index: aIndex } = a
        const { fixed: bFixed, index: bIndex } = b
        if (
          (aFixed === "left" && bFixed !== "left") ||
          (bFixed === "right" && aFixed !== "right")
        ) {
          return -2
        }
        if (
          (bFixed === "left" && aFixed !== "left") ||
          (aFixed === "right" && bFixed !== "right")
        ) {
          return 2
        }
        // å¦‚æœæ²¡æœ‰indexï¼Œåœ¨ dataIndex æˆ–è€… key ä¸å­˜åœ¨çš„æ—¶å€™ä»–ä¼šæŠ¥é”™
        const aKey = `${genColumnKey(a.key, a.dataIndex, aIndex)}_${aIndex}`
        const bKey = `${genColumnKey(b.key, b.dataIndex, bIndex)}_${bIndex}`
        return keys.indexOf(aKey) - keys.indexOf(bKey)
      })
    }
    if (tableColumn && tableColumn.length > 0) {
      counter.setColumns(tableColumn)
    }
  }, [counter.columnsMap, counter.sortKeyColumns.join("-")])

  /**
   * åŒæ­¥ Paginationï¼Œæ”¯æŒå—æ§çš„ é¡µç  å’Œ pageSize
   */
  useDeepCompareEffect(() => {
    if (
      propsPagination &&
      propsPagination.current &&
      propsPagination.pageSize
    ) {
      action.setPageInfo({
        pageSize: propsPagination.pageSize,
        page: propsPagination.current
      })
    }
  }, [propsPagination])

  // æ˜ å°„ selectedRowKeys ä¸ selectedRow
  useEffect(() => {
    if (action.loading !== false || propsRowSelection === false) {
      return
    }
    const tableKey = rest.rowKey

    // dataSource maybe is a null
    // eg: api has 404 error
    const duplicateRemoveMap = new Map()
    if (Array.isArray(dataSource)) {
      // æ ¹æ®å½“å‰é€‰ä¸­å’Œå½“å‰çš„æ‰€æœ‰æ•°æ®è®¡ç®—é€‰ä¸­çš„è¡Œ
      // å› ä¸ºé˜²æ­¢ç¿»é¡µä»¥åä¸¢å¤±ï¼Œæ‰€æœ‰è¿˜å¢åŠ äº†å½“å‰é€‰æ‹©é€‰ä¸­çš„
      const rows = [...dataSource, ...selectedRows].filter((item, index) => {
        let rowKey = tableKey
        if (!tableKey) {
          return selectedRowKeys.includes(index)
        }
        if (typeof tableKey === "function") {
          rowKey = tableKey(item, index)
        } else {
          rowKey = item[tableKey]
        }
        if (duplicateRemoveMap.has(rowKey)) {
          return false
        }
        duplicateRemoveMap.set(rowKey, true)
        return selectedRowKeys.includes(rowKey)
      })
      setSelectedRows(rows)
      return
    }
    setSelectedRows([])
  }, [selectedRowKeys.join("-"), action.loading, propsRowSelection === false])

  const rowSelection = {
    selectedRowKeys,
    ...propsRowSelection,
    onChange: (keys, rows) => {
      if (propsRowSelection && propsRowSelection.onChange) {
        propsRowSelection.onChange(keys, rows)
      }
      setSelectedRowKeys([...keys])
    }
  }

  useEffect(() => {
    counter.setTableSize(rest.size || "middle")
  }, [rest.size])

  if (counter.columns.length < 1) {
    return (
      <Card bordered={false} bodyStyle={{ padding: 50 }}>
        <Empty />
      </Card>
    )
  }

  const className = classNames(defaultClassName, propsClassName)
  const toolbarDom = toolBarRender !== false &&
    (options !== false || headerTitle || toolBarRender) && (
      // if options= false & headerTitle=== false, hide Toolbar
      <Toolbar
        options={options}
        headerTitle={headerTitle}
        action={action}
        onSearch={keyword => {
          if (options && options.search) {
            const { name = "keyword" } =
              options.search === true
                ? {
                    name: "keyword"
                  }
                : options.search
            setFormSearch({
              ...formSearch,
              [name]: keyword
            })
          }
        }}
        selectedRows={selectedRows}
        selectedRowKeys={selectedRowKeys}
        toolBarRender={toolBarRender}
      />
    )
  const alertDom = propsRowSelection !== false && (
    <Alert
      selectedRowKeys={selectedRowKeys}
      selectedRows={selectedRows}
      onCleanSelected={onCleanSelected}
      alertOptionRender={rest.tableAlertOptionRender}
      alertInfoRender={tableAlertRender}
    />
  )

  const tableDom = (
    <Table
      {...rest}
      size={counter.tableSize}
      rowSelection={propsRowSelection === false ? undefined : rowSelection}
      className={tableClassName}
      style={tableStyle}
      columns={counter.columns.filter(item => {
        // åˆ æ‰ä¸åº”è¯¥æ˜¾ç¤ºçš„
        const { key, dataIndex } = item
        const columnKey = genColumnKey(key, dataIndex)
        if (!columnKey) {
          return true
        }
        const config = counter.columnsMap[columnKey]
        if (config && config.show === false) {
          return false
        }
        return true
      })}
      loading={action.loading || props.loading}
      dataSource={dataSource}
      pagination={pagination}
      onChange={(changePagination, filters, sorter, extra) => {
        if (rest.onChange) {
          rest.onChange(changePagination, filters, sorter, extra)
        }

        // åˆ¶é€ ç­›é€‰çš„æ•°æ®
        setProFilter(removeObjectNull(filters))

        // åˆ¶é€ ä¸€ä¸ªæ’åºçš„æ•°æ®
        if (Array.isArray(sorter)) {
          const data = sorter.reduce((pre, value) => {
            if (!value.order) {
              return pre
            }
            return {
              ...pre,
              [`${value.field}`]: value.order
            }
          }, {})
          setProSort(data)
        } else if (sorter.order) {
          setProSort({ [`${sorter.field}`]: sorter.order })
        }
      }}
    />
  )
  /**
   * table åŒºåŸŸçš„ domï¼Œä¸ºäº†æ–¹ä¾¿ render
   */
  const tableAreaDom = (
    <>
      {toolbarDom}
      {alertDom}
      {tableDom}
    </>
  )

  const renderTable = () => {
    if (props.tableRender) {
      return props.tableRender(props, tableAreaDom, {
        toolbar: toolbarDom || undefined,
        alert: alertDom || undefined,
        table: tableDom || undefined
      })
    }
    return tableAreaDom
  }

  return (
    <ConfigProvider getPopupContainer={() => rootRef.current || document.body}>
      <div
        className={className}
        id="ant-design-pro-table"
        style={style}
        ref={rootRef}
      >
        {(search || type === "form") && (
          <FormSearch
            {...rest}
            type={props.type}
            formRef={formRef}
            onSubmit={value => {
              if (type !== "form") {
                setFormSearch(
                  beforeSearchSubmit({
                    ...value,
                    _timestamp: Date.now()
                  })
                )
                // back first page
                action.resetPageIndex()
              }

              if (props.onSubmit) {
                props.onSubmit(value)
              }
            }}
            onReset={() => {
              setFormSearch(beforeSearchSubmit({}))
              // back first page
              action.resetPageIndex()
              onReset()
            }}
            dateFormatter={rest.dateFormatter}
            search={search}
          />
        )}
        {/* æ¸²æŸ“ä¸€ä¸ªé¢å¤–çš„åŒºåŸŸï¼Œç”¨äºä¸€äº›è‡ªå®šä¹‰ */}
        {type !== "form" && props.tableExtraRender && (
          <div className={`${className}-extra`}>
            {props.tableExtraRender(props, dataSource)}
          </div>
        )}
        {type !== "form" && (
          <Card
            bordered={false}
            style={{
              height: "100%"
            }}
            bodyStyle={{
              padding: 0
            }}
          >
            {renderTable()}
          </Card>
        )}
      </div>
    </ConfigProvider>
  )
}

/**
 * ğŸ† Use Ant Design Table like a Pro!
 * æ›´å¿« æ›´å¥½ æ›´æ–¹ä¾¿
 * @param props
 */
const ProviderWarp = props => (
  <Container.Provider initialState={props}>
    <ConfigConsumer>
      {({ getPrefixCls }) => (
        <IntlConsumer>
          {value => (
            <IntlProvider value={value}>
              <ErrorBoundary>
                <ProTable
                  defaultClassName={getPrefixCls("pro-table")}
                  {...props}
                />
              </ErrorBoundary>
            </IntlProvider>
          )}
        </IntlConsumer>
      )}
    </ConfigConsumer>
  </Container.Provider>
)

export default ProviderWarp
