import { EllipsisOutlined } from "@ant-design/icons"
import {
  LightFilter,
  ProFormDatePicker,
  ProTable
} from "@ant-design/pro-components"
import { Button } from "antd"

const tableListDataSource = []

const creators = ["付小小", "曲丽丽", "林东东", "陈帅帅", "兼某某"]

for (let i = 0; i < 5; i += 1) {
  tableListDataSource.push({
    key: i,
    name: `AppName ${i+1}`,
    containers: Math.floor(Math.random() * 20),
    creator: creators[Math.floor(Math.random() * creators.length)]
  })
}

const columns = [
    {
        title: "Index",
        dataIndex: "index",
        render: _ => <a>{_}</a>
      },
  {
    title: "Name",
    dataIndex: "name",
    render: _ => <a>{_}</a>
  },
  {
    title: "Day of birth",
    dataIndex: "containers",
    align: "right",
    sorter: (a, b) => a.containers - b.containers
  },
  {
    title: "创建者",
    dataIndex: "creator",
    valueType: "select",
    valueEnum: {
      all: { text: "全部" },
      付小小: { text: "付小小" },
      曲丽丽: { text: "曲丽丽" },
      林东东: { text: "林东东" },
      陈帅帅: { text: "陈帅帅" },
      兼某某: { text: "兼某某" }
    }
  },
  {
    title: "操作",
    key: "option",
    width: 120,
    valueType: "option",
    render: () => [
      <a key="link">链路</a>,
      <a key="warn">报警</a>,
      <a key="more">
        <EllipsisOutlined />
      </a>
    ]
  }
]

function Student()  {
  return (
    <ProTable
      columns={columns}
      request={(params, sorter, filter) => {
        // 表单搜索项会从 params 传入，传递给后端接口。
        console.log(params, sorter, filter)
        return Promise.resolve({
          data: tableListDataSource,
          success: true
        })
      }}
      toolbar={{
        search: {
          onSearch: value => {
            alert(value)
          }
        },
        // filter: (
        //   <LightFilter>
        //     <ProFormDatePicker name="startdate" label="响应日期" />
        //   </LightFilter>
        // ),
        actions: [
          <Button
            key="primary"
            type="primary"
            onClick={() => {
              alert("add")
            }}
          >
            添加
          </Button>
        ]
      }}
      rowKey="key"
      search={false}
    />
  )
}
export default  Student
