import { EllipsisOutlined } from "@ant-design/icons"
import {
    LightFilter,
    ProFormDatePicker,
    ProTable
} from "@ant-design/pro-components"
import { Button, Table } from "antd"
import { useState } from "react"
import en_US from 'antd/es/locale/en_US';

const tableListDataSource = [ 
  // data source here
]

for (let i = 0; i < 6; i++) {
  tableListDataSource[i].index = i + 1;
}

const enLocale = {
  columnSetting: 'Settings',
}

const columns = [
  // columns here
]

function Student() {
  const [searchText, setSearchText] = useState("");
  const [searchValue, setSearchValue] = useState('');

  const handleSearch = (selectedKeys, confirm, dataIndex) => {
    confirm();
    setSearchText(selectedKeys[0])
    setSearchValue(dataIndex)
  };

  const handleReset = (clearFilters) => {
    clearFilters();
    setSearchText("")
  };

  const filteredData = tableListDataSource.filter((item) => {
    return item[searchValue].toLowerCase().includes(searchText.toLowerCase())
  })

  return (
    <ProTable
      locale={en_US}
      columns={columns}
      dataSource={filteredData}
      search={{
        filterType: "light",
        placeholder: "Search name, hometown...",
        onFilter: (value, record) =>
          record[searchValue].toString().toLowerCase().includes(value.toLowerCase()),
      }}
      onReset={() => setSearchText("")}
      rowKey="index"
    />
  );
}
