import { EllipsisOutlined } from "@ant-design/icons"
import {
  LightFilter,
  ProFormDatePicker,
  ProTable
} from "@ant-design/pro-components"
import { Button } from "antd"

const tableListDataSource = [
    {
        name: "Lê Bùi Văn",
        dob: "22/06/2002",
        gender: "Nam",
        class: "A",
        phone: "0123456789",
        hometown: "Hà Nội",
        subject: ["Toán", "Vật lý", "Tiếng Anh"]
      },
      {
        name: "Nguyễn Thị Kim Ngân",
        dob: "09/03/2003",
        gender: "Nữ",
        class: "B",
        phone: "0112345678",
        hometown: "Bắc Ninh",
        subject: ["Văn", "Hóa học", "Lịch sử"]
      },
      {
        name: "Trần Văn Minh",
        dob: "05/11/2002",
        gender: "Nam",
        class: "C",
        phone: "0109876543",
        hometown: "Thái Bình",
        subject: ["Toán", "Vật lý", "Sinh học"]
      },
      {
        name: "Phạm Thị Thu",
        dob: "17/02/2002",
        gender: "Nữ",
        class: "A",
        phone: "0198765432",
        hometown: "Hải Phòng",
        subject: ["Văn", "Địa lí", "Tiếng Anh"]
      },
      {
        name: "Nguyễn Văn Hoàn",
        dob: "12/09/2003",
        gender: "Nam",
        class: "B",
        phone: "0187654321",
        hometown: "Hưng Yên",
        subject: ["Toán", "Vật lý", "Hóa học"]
      },
      {
        name: "Bùi Ngọc Ánh",
        dob: "08/06/2002",
        gender: "Nữ",
        class: "C",
        phone: "0176543210",
        hometown: "Nghệ An",
        subject: ["Toán", "Hóa học", "Sinh học"]
      }
]
for (let i = 0; i < 6;i++) {
    tableListDataSource[i].index = i +1;
}



const columns = [
    {
        title: "Index",
        dataIndex: "index",
        width: 60,
      },
  {
    title: "Name",
    dataIndex: "name",
    width: 200,

    render: _ => <a>{_}</a>
  },
  {
    title: "Day of birth",
    dataIndex: "dob",
  },
  {
    title: "Gender",
    dataIndex: "gender",
  },
  {
    title: "Class",
    dataIndex: "class",
  },
  {
    title: "Hometown",
    dataIndex: "hometown",
  },
  {
    title: "Phone Number",
    dataIndex: "class",
  },
  {
    title: "Class",
    dataIndex: "class",
  },
  {
    title: "Action",
    key: "option",
    width: 120,
    valueType: "option",
    render: () => [
      <a key="edit">Edit</a>,
      <a key="delete">Delete</a>,
    ]
  }
]

function Student()  {
  return (
    <ProTable
      columns={columns}
      request={(params, sorter, filter) => {
        // 表单搜索项会从 params 传入，传递给后端接口。
        console.log(params, sorter, filter)
        return Promise.resolve({
          data: tableListDataSource,
          success: true
        })
      }}
      toolbar={{
        search: {
            placeholder: 'Search in here',
          onSearch: value => {
            alert(value)
          }
        },
        // filter: (
        //   <LightFilter>
        //     <ProFormDatePicker name="startdate" label="响应日期" />
        //   </LightFilter>
        // ),
        actions: [
          <Button
            key="primary"
            type="primary"
            onClick={() => {
              alert("add")
            }}  
          >
            Add
          </Button>
        ]
      }}
      rowKey="key"
      search={false}
      dateFormatter="string"
      
    />
  )
}
export default  Student
