import { Button, Modal, Form, Input, Table } from "antd";
import { AudioOutlined } from '@ant-design/icons';
import { useState } from "react";
import { SearchOutlined } from '@ant-design/icons';
import AddStudent from "./addStudent";
import tableListStudent from "./listStudent";
const { Search } = Input;


let totalStudent = tableListStudent.length
for (let i = 0; i < tableListStudent.length; i++) {
  tableListStudent[i].index = i + 1;
}
const columns = [
  {
    title: "Index",
    dataIndex: "index",
    width: 30,
    align: "center"

  },
  {
    title: "Name",
    dataIndex: "name",
    width: 170,
    align: "left",

    render: _ => <a>{_}</a>,
  },
  {
    title: "Day of birth",
    dataIndex: "dob",
    align: "center",
    width: 120,

  },
  {
    title: "Gender",
    dataIndex: "gender",
    align: "center",
    width: 100,

  },
  {
    title: "Class",
    dataIndex: "class",
    align: "center",
    width: 100,

  },
  {
    title: "Hometown",
    dataIndex: "hometown",
    align: "center",
    width: 120,

  },
  {
    title: "Phone Number",
    dataIndex: "phone",
    align: "center",
    width: 120,

  },
  {
    title: "Subject",
    dataIndex: "subjects",
    align: "center",
    render: (subjects) => (
      <>
        {

          subjects.join(' - ')
        }
      </>
    )
  },
  {
    title: "Action",
    align: "center",
    key: "option",
    width: 200,
    valueType: "option",
    render: (text,record) => [
      <Button >Edit</Button>,
      <Button onClick={() => {
        console.log(tableListStudent[record.index-1])
        delete tableListStudent[record.index-1];
      }} >Delete</Button>,
    ]
  }
]
// delete 
const handleDelete = (index) => {
 
  return index 
  
};
// seetting for search
const suffix = (
  <AudioOutlined
    style={{

      fontSize: 16,
      color: '#1890ff',
    }}
  />
);



function Student() {
  const [isModalVisible, setIsModalVisible] = useState(false);

  const showModal = () => {
    setIsModalVisible(true);
  };

  const handleOk = () => {
    setIsModalVisible(false);
  };

  const handleCancel = () => {
    setIsModalVisible(false);
  };

  const onFinish = (values) => {
    console.log('Form submitted:', values);
    setIsModalVisible(false);
  };
  const [searchText, setSearchText] = useState('');
  const [searchedColumn, setSearchedColumn] = useState('');
  const [addStudentComponent,setAddStudentComponent] = useState(false)
  const [form] = Form.useForm();
  const [tableListStudentArray,settableListStudent] = [tableListStudent]
//   const onFinish = (values) => {
//     // if (values.name === '') return alert("Vui lòng điền đầy đủ thông tin ")
//     if (values == null ) {console.log(values)}
    
//     const newData = [...tableListStudentArray];
//     tableListStudent.push(values);
//     tableListStudentArray.push(values);
//     settableListStudent(newData);
//     form.resetFields();
// };

  const handleClick=() => {
    setAddStudentComponent(true)
  }

  
  // const getColumnSearchProps = (dataIndex) => ({
  //   // tạo một ô tìm kiếm 
  //   filterDropdown: ({ setSelectedKeys, selectedKeys, confirm, clearFilters }) => (
  //     <div style={{ padding: 8 }}>
  //       <Input
  //         placeholder={`Search ${dataIndex}`}
  //         value={selectedKeys[0]}
  //         onChange={(e) => setSelectedKeys(e.target.value ? [e.target.value] : [])}
  //         onPressEnter={() => confirm()}
  //         style={{ width: 188, marginBottom: 8, display: 'block' }}
  //       />
  //       <Space>
  //         <Button
  //           type="primary"
  //           onClick={() => confirm()}
  //           icon={<SearchOutlined />}
  //           size="small"
  //           style={{ width: 90 }}
  //         >
  //           Search
  //         </Button>
  //         <Button onClick={() => clearFilters()} size="small" style={{ width: 90 }}>
  //           Reset
  //         </Button>
  //       </Space>
  //     </div>
  //   ),
  //   // tạo một icon để biểu thị trạng thái của ô tìm kiếm
  //   filterIcon: (filtered) => <SearchOutlined style={{ color: filtered ? '#1890ff' : undefined }} />,
  //   //xử lý việc tìm kiếm.
  //   onFilter: (value, record) =>
  //     record[dataIndex] ? record[dataIndex].toString().toLowerCase().includes(value.toLowerCase()) : '',
  //   //   xử lý việc hiển thị ô tìm kiếm.
  //   onFilterDropdownVisibleChange: (visible) => {
  //     if (visible) {
  //       setTimeout(() => {
  //         document.getElementById('search-input').select();
  //       }, 100);
  //     }
  //   },
  //   // hiển thị kết quả tìm kiếm cho cột
  //   render: (text) =>
  //     searchedColumn === dataIndex ? (
  //       <span style={{ fontWeight: 'bold' }}>{text}</span>
  //     ) : (
  //       text
  //     ),
  // });
  // const handleSearch = (selectedKeys, confirm, dataIndex) => {
  //   confirm();
  //   setSearchText(selectedKeys[0]);
  //   setSearchedColumn(dataIndex);
  // };

  // const handleReset = (clearFilters) => {
  //   clearFilters();
  //   setSearchText('');
  // };
  const handleHideForm = () => {
    setAddStudentComponent(false)
  }
  
  return (
    <div classname = "studentContainer">
      <div className="justify-between flex">
        <Search className="w-80"
          placeholder="Search in here..."
          enterButton={
            <Button
              style={{
                color: "#fff",
                backgroundColor: "#1677ff",
                boxShadow: "0 2px 0 rgba(5, 145, 255, 0.1)"
              }} >
              Search
            </Button>
 
          }
          value={searchText}
          onChange={(e) => {
            var text = e.target.value;
          
            return setSearchText(text)
          }}
          onPressEnter={(value) => setSearchText(value)}

          size="large"
          suffix={suffix}
        />
       

        <Button className="mr-12" onClick={showModal}>
        Add new student Thanh
      </Button>
        
      </div>


      <Table
        columns={columns}
        
        dataSource={tableListStudentArray.filter((item) => item.name.toLowerCase().replace(/[àáảãạâầấẩẫậăằắẳẵặ]/g, "a")
          .replace(/[èéẻẽẹêềếểễệ]/g, "e")
          .replace(/[ìíỉĩị]/g, "i")
          .replace(/[òóỏõọôồốổỗộơờớởỡợ]/g, "o")
          .replace(/[ùúủũụưừứửữự]/g, "u")
          .replace(/[ỳýỷỹỵ]/g, "y")
          .replace(/[đ]/g, "d")
          .replace(/\s+/g, " ")
          .replace(/[^A-Za-z0-9 ]/g, "").includes(searchText.toLowerCase().replace(/[àáảãạâầấẩẫậăằắẳẵặ]/g, "a")
          .replace(/[èéẻẽẹêềếểễệ]/g, "e")
          .replace(/[ìíỉĩị]/g, "i")
          .replace(/[òóỏõọôồốổỗộơờớởỡợ]/g, "o")
          .replace(/[ùúủũụưừứửữự]/g, "u")
          .replace(/[ỳýỷỹỵ]/g, "y")
          .replace(/[đ]/g, "d")
          .replace(/\s+/g, " ")
          .replace(/[^A-Za-z0-9 ]/g, "")))}
        pagination={{
          showTotal: (total, range) => ` ${range[0]}-${range[1]}/ Tổng ${total}`,
          defaultPageSize: 5,


        }}
        
        
      />
    {/* {addStudentComponent &&(
    <div className="absolute top-0 right-0 left-0 bottom-0 backdrop-filter backdrop-blur-sm flex justify-center items-center h-screen ">
            <Form className="flex justify-center items-center h-screen  w-80 h-min backdrop-opacity-25 bg-white rounded-lg shadow-md border border-gray-300" form={form} onFinish={onFinish} layout="inline">
                
               
            </Form>
        </div>
        )} */}
        <Modal
        title="Register New Student"
        // visible={isModalVisible}
        onOk={handleOk}
        onCancel={handleCancel}
        footer={[
          <Button key="cancel" onClick={handleCancel}>
            Cancel
          </Button>,
          <Button key="submit" type="primary" form="myForm" htmlType="submit">
            Submit
          </Button>,
        ]}
      >
        <Form id="myForm" onFinish={onFinish}>
        <Form.Item className="w-10/12 my-1" name="name">
                    Name
                    <Input placeholder="Name" type="text" />
                </Form.Item>
                <Form.Item className="w-10/12 my-1" name="dob">
                Day of birthday
                    <Input placeholder="Day of birthday "  type="date"/>
                </Form.Item>
                <Form.Item className="w-10/12 my-1" name="gender">
                Gender
                    <Input placeholder="Gender" />
                </Form.Item>
                <Form.Item className="w-10/12 my-1" name="class">Class
                    <Input placeholder="Class" />
                </Form.Item>
                <Form.Item className="w-10/12 my-1" name="phone">Phone Number
                    <Input placeholder="Phone Number" />
                </Form.Item>
                <Form.Item className="w-10/12 my-1" name="hometown">Hometown
                    <Input placeholder="Hometown" />
                </Form.Item>
                <Form.Item className="w-10/12 my-1" name="subject">Subject
                    <Input placeholder="Subject" />
                </Form.Item>
        </Form>
      </Modal>
    
    </div>



  )
}
export default Student
