import { EllipsisOutlined, PlusOutlined } from "@ant-design/icons"
import { ProTable, TableDropdown } from "@ant-design/pro-components"
import { Button, Dropdown, Space, Tag } from "antd"
import { useRef } from "react"
import request from "umi-request"
export const waitTimePromise = async (time = 100) => {
  return new Promise(resolve => {
    setTimeout(() => {
      resolve(true)
    }, time)
  })
}

export const waitTime = async (time = 100) => {
  await waitTimePromise(time)
}

const columns = [
  {
    dataIndex: "index",
    valueType: "indexBorder",
    width: 48
  },
  {
    title: "标题",
    dataIndex: "title",
    copyable: true,
    ellipsis: true,
    tip: "标题过长会自动收缩",
    formItemProps: {
      rules: [
        {
          required: true,
          message: "此项为必填项"
        }
      ]
    }
  },
  {
    disable: true,
    title: "状态",
    dataIndex: "state",
    filters: true,
    onFilter: true,
    ellipsis: true,
    valueType: "select",
    valueEnum: {
      all: { text: "超长".repeat(50) },
      open: {
        text: "未解决",
        status: "Error"
      },
      closed: {
        text: "已解决",
        status: "Success",
        disabled: true
      },
      processing: {
        text: "解决中",
        status: "Processing"
      }
    }
  },
  
]

function Student() {
    const actionRef = useRef()
    return (
        <ProTable
          columns={columns}
          actionRef={actionRef}
          cardBordered
          request={async (params = {}, sort, filter) => {
            console.log(sort, filter)
            await waitTime(2000)
            return request("https://proapi.azurewebsites.net/github/issues", {
              params
            })
          }}
          editable={{
            type: "multiple"
          }}
          columnsState={{
            persistenceKey: "pro-table-singe-demos",
            persistenceType: "localStorage",
            onChange(value) {
              console.log("value: ", value)
            }
          }}
          rowKey="id"
          search={{
            labelWidth: "auto"
          }}
          options={{
            setting: {
              listsHeight: 400
            }
          }}
          form={{
            // 由于配置了 transform，提交的参与与定义的不同这里需要转化一下
            syncToUrl: (values, type) => {
              if (type === "get") {
                return {
                  ...values,
                  created_at: [values.startTime, values.endTime]
                }
              }
              return values
            }
          }}
          pagination={{
            pageSize: 5,
            onChange: page => console.log(page)
          }}
          dateFormatter="string"
          headerTitle="高级表格"
          toolBarRender={() => [
            <Button
              key="button"
              icon={<PlusOutlined />}
              onClick={() => {
                actionRef.current?.reload()
              }}
              type="primary"
            >
              新建
            </Button>,
            <Dropdown
              key="menu"
              menu={{
                items: [
                  {
                    label: "1st item",
                    key: "1"
                  },
                  {
                    label: "2nd item",
                    key: "1"
                  },
                  {
                    label: "3rd item",
                    key: "1"
                  }
                ]
              }}
            >
              <Button>
                <EllipsisOutlined />
              </Button>
            </Dropdown>
          ]}
        />
      )
   
}

export default Student
