import { Table, Button, Input,Space } from "antd";
import { AudioOutlined } from '@ant-design/icons';
import { useState } from "react";

const { Search } = Input;

var tableListStudent = [
    {
        name: "Lê Bùi Văn",
        dob: "22/06/2002",
        gender: "Nam",
        class: "A",
        phone: "0123456789",
        hometown: "Hà Nội",
        subjects: ["Toán", "Vật lý", "Tiếng Anh"]
    },
    {
        name: "Nguyễn Thị Kim Ngân",
        dob: "09/03/2003",
        gender: "Nữ",
        class: "B",
        phone: "0112345678",
        hometown: "Bắc Ninh",
        subjects: ["Văn", "Hóa học", "Lịch sử"]
    },
    {
        name: "Trần Văn Minh",
        dob: "05/11/2002",
        gender: "Nam",
        class: "C",
        phone: "0109876543",
        hometown: "Thái Bình",
        subjects: ["Toán", "Vật lý", "Sinh học"]
    },
    {
        name: "Phạm Thị Thu",
        dob: "17/02/2002",
        gender: "Nữ",
        class: "A",
        phone: "0198765432",
        hometown: "Hải Phòng",
        subjects: ["Văn", "Địa lí", "Tiếng Anh"]
    },
    {
        name: "Nguyễn Văn Hoàn",
        dob: "12/09/2003",
        gender: "Nam",
        class: "B",
        phone: "0187654321",
        hometown: "Hưng Yên",
        subjects: ["Toán", "Vật lý", "Hóa học"]
    },
    {
        name: "Bùi Ngọc Ánh",
        dob: "08/06/2002",
        gender: "Nữ",
        class: "C",
        phone: "0176543210",
        hometown: "Nghệ An",
        subjects: ["Toán", "Hóa học", "Sinh học"]
    }
]
let totalStudent = tableListStudent.length
for (let i = 0; i < tableListStudent.length; i++) {
    tableListStudent[i].index = i + 1;
}
const columns = [
    {
        title: "Index",
        dataIndex: "index",
        width: 30,
        align: "center"
    },
    {
        title: "Name",
        dataIndex: "name",
        width: 170,
        align: "left",
        render: _ => <a>{_}</a>
    },
    {
        title: "Day of birth",
        dataIndex: "dob",
        align: "center",
        width: 120,

    },
    {
        title: "Gender",
        dataIndex: "gender",
        align: "center",
        width: 100,

    },
    {
        title: "Class",
        dataIndex: "class",
        align: "center",
        width: 100,

    },
    {
        title: "Hometown",
        dataIndex: "hometown",
        align: "center",
        width: 120,

    },
    {
        title: "Phone Number",
        dataIndex: "phone",
        align: "center",
        width: 120,

    },
    {
        title: "Subject",
        dataIndex: "subjects",
        align: "center",
        render: (subjects) => (
            <>
                {

                    subjects.join(' - ')
                }
            </>
        )
    },
    {
        title: "Action",
        align: "center",
        key: "option",
        width: 200,
        valueType: "option",
        render: () => [
            <Button >Edit</Button>,
            <Button >Delete</Button>,
        ]
    }
]

// seetting for search
const suffix = (
    <AudioOutlined
      style={{
        
        fontSize: 16,
        color: '#1890ff',
      }}
    />
  );
  
  const getColumnSearchProps = (dataIndex) => ({
    filterDropdown: ({ setSelectedKeys, selectedKeys, confirm, clearFilters }) => (
      <div style={{ padding: 8 }}>
        <Input
          placeholder={`Search ${dataIndex}`}
          value={selectedKeys[0]}
          onChange={(e) => setSelectedKeys(e.target.value ? [e.target.value] : [])}
          onPressEnter={() => confirm()}
          style={{ width: 188, marginBottom: 8, display: 'block' }}
        />
        <Space>
          <Button
            type="primary"
            onClick={() => confirm()}
            icon={<SearchOutlined />}
            size="small"
            style={{ width: 90 }}
          >
            Search
          </Button>
          <Button onClick={() => clearFilters()} size="small" style={{ width: 90 }}>
            Reset
          </Button>
        </Space>
      </div>
    ),
    filterIcon: (filtered) => <SearchOutlined style={{ color: filtered ? '#1890ff' : undefined }} />,
    onFilter: (value, record) =>
      record[dataIndex] ? record[dataIndex].toString().toLowerCase().includes(value.toLowerCase()) : '',
    onFilterDropdownVisibleChange: (visible) => {
      if (visible) {
        setTimeout(() => {
          document.getElementById('search-input').select();
        }, 100);
      }
    },
    render: (text) =>
      searchedColumn === dataIndex ? (
        <span style={{ fontWeight: 'bold' }}>{text}</span>
      ) : (
        text
      ),
  });

function Student() {
    const onSearch = (value) => console.log(value);
    const [searchText, setSearchText]= useState('');
    const handleOnChange=(event) => {
        setSearchText(event.target.value)
      console.log(event.target.value)
    };
    return (
        <div >
            <div className="justify-between flex">
            <Search className="w-80"
                placeholder="Search in here..."
                enterButton={
                    <Button
                      style={{
                        color: "#fff",
                        backgroundColor: "#1677ff",
                        boxShadow: "0 2px 0 rgba(5, 145, 255, 0.1)"
                      }} >
                      Search
                    </Button>
                  }

                size="large"
                suffix={suffix}
                onSearch={onSearch}
                onChange = {handleOnChange}
            />
            <Button className="mr-12"> Add</Button>
            </div>
            
            
            <Table
                columns={columns}
                dataSource={tableListStudent}
                pagination={{
                    showTotal: (total, range) => ` ${range[0]}-${range[1]}/ Tổng ${total}`,
                    defaultPageSize: 5

                }}
            />
        </div>



    )
}
export default Student
