import { Table, Button, Input, Form } from "antd";
import { useState } from "react";

function AddStudent() {
    const [form] = Form.useForm();
    const [tableData, setTableData] = useState([]);

    const onFinish = (values) => {
        const newData = [...tableData];
        newData.push(values);
        setTableData(newData);
        form.resetFields();
    };

    return (
        <> <div className="absolute top-0 right-0 left-0 bottom-0 backdrop-filter backdrop-blur-sm flex justify-center items-center h-screen ">
            <Form className="" form={form} onFinish={onFinish} layout="inline">
                <Form.Item name="name">
                    <Input placeholder="Name" type="text" />
                </Form.Item>
                <Form.Item name="dob">
                    <Input placeholder="Day of birthday "  type="date"/>
                </Form.Item>
                <Form.Item name="gender">
                    <Input placeholder="Gender" />
                </Form.Item>
                <Form.Item name="class">
                    <Input placeholder="Class" />
                </Form.Item>
                <Form.Item name="phone">
                    <Input placeholder="Phone Number" />
                </Form.Item>
                <Form.Item name="hometown">
                    <Input placeholder="Hometown" />
                </Form.Item>
                <Form.Item>
                    <Button htmlType="submit">Add</Button>
                </Form.Item>
            </Form>
        </div>

        </>
    );
}
export default AddStudent;
